const data = new Map();
function get(key) {
    const found = data.has(key);
    if (!found)
        return;
    return data.get(key);
}
function set(key, value) {
    data.set(key, value);
}
function remove(key) {
    const found = data.has(key);
    if (!found)
        return;
    data.delete(key);
}
export const baseURL = process.cwd();
export const pids = new Set([]);
export const store = {
    get,
    set,
    remove
};
